INSERT INTO `wp_posts` VALUES(217, 1, '2012-10-15 06:18:47', '2012-10-15 06:18:47', 'Cantinas Ranch Foundation is non-profit 501(C)(3) entity. The foundations’ mission is twofold: (a) to redeem the arts, by encouraging talented Christian teen and college-level artists to trust God and follow his lead in their creative pursuits, and (b) to promote healthy family values and interaction.\r\n \r\nCantinas Ranch Foundation does this through a variety of programs, including the Cantinas Music Festival, Celebration of the Arts and Ministry Outreach.', 'Info About 501 (C)(3)', '', 'publish', 'open', 'open', '', 'info-about-501-c3', '', '', '2012-10-15 10:48:39', '2012-10-15 10:48:39', '', 4, 'http://10.0.1.9:8888/cantinasranch/?page_id=217', 0, 'page', '', 0);
