INSERT INTO `wp_posts` VALUES(224, 1, '2012-10-15 06:21:44', '2012-10-15 06:21:44', 'God willing, we will endeavor to be "filling the pots" by equipping participants to enter into a new dimension of intimacy and revelation of Jesus Christ through worship, and then be inspired to pass on what they have received at camp. In essence, they will be "spilling over" with Living Water into other believers'' lives. Worship will be expressed through multiple expressions of the "beauty of God" through the arts.', 'Mission Statement', '', 'publish', 'open', 'open', '', 'mission-statement', '', '', '2012-10-20 20:13:30', '2012-10-20 20:13:30', '', 4, 'http://10.0.1.9:8888/cantinasranch/?page_id=224', 0, 'page', '', 0);
