INSERT INTO `wp_posts` VALUES(387, 1, '2012-10-15 08:26:06', '2012-10-15 08:26:06', 'Mike graduated from Whitworth University in 1969 with a degree in Business Administration and joined the management team with AT&T. He left the corporate world after 25 years and for the last 18 years has worked for Hume Lake Christian Camps where he currently serves as the Senior Director of Development. While with Hume he has served also as the Director of Operations and the Assistant Executive Director. He has been married to his wife Glenda for 43 years and they reside at the camp where she manages the Hume Lake Gift Shop and Book Store. Mike also serves on the Boards of the Christian Camp & Conference Association – Sierra Pacific region, the Giant Sequoia National Monument Association and as President of the Hume Lake Charter School Board.', 'Mike Drake', '', 'inherit', 'open', 'open', '', '281-revision-2', '', '', '2012-10-15 08:26:06', '2012-10-15 08:26:06', '', 281, 'http://10.0.1.9:8888/cantinasranch/281-revision-2/', 0, 'revision', '', 0);
